import "./bpmn.scss";
import BpmnModelerComponent from "./bpmn.modeler.component";
import 'bpmn-js-properties-panel/dist/assets/bpmn-js-properties-panel.css';
import { Link, useLocation } from "react-router-dom";
import axios from 'axios';
import React, { useEffect, useState } from "react";


function Bpmn(props) {
  let location = useLocation();
  let bpmn = location.pathname.substring(6);
  const XMLData = `http://localhost:8080/IoTAware/${bpmn}`;
  
  const [bpmnString, setBpmnString] = useState("");

  useEffect(() => {
    axios.get(XMLData, {
      "Content-Type": "application/xml; charset=utf-8"
    })
      .then((response) => {
        // console.log('Your xml file as string', response.data);
        setBpmnString(response.data)
      })
      .catch((error) => console.log(error))
  }, [])

  if(bpmnString == "") {
    return null
  }
  else 
  return (
    <div className="bpmn" id="bpmn">
      <h1>Bpmn Modeler</h1>
      <div className="container">
        <BpmnModelerComponent xml={`http://localhost:8080/IoTAware/${bpmn}`} bpmnString={bpmnString} />
      </div>
    </div>
  );
}

export default Bpmn;
